((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'Escape from Tarkov', 'https://arbcdn.techfast.vn/c09c2b7c2b0b45859223a459a2048553.webp', 'Escape from Tarkov delivers a hardcore survival FPS adventure with its realistic combat and deep progression, celebrating a unique tactical experience.'),
((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'inZOI', 'https://arbcdn.techfast.vn/331efd35d3e9457db80ab9e3ca2a90e5.webp', 'inZOI offers a visually stunning life sim with deep customization, bolstered by Island Getaway’s fresh content, challenging The Sims’ throne.'),
((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'The Headliners', 'https://arbcdn.techfast.vn/09182ad05172417eb3d557c95e8b6c58.webp', 'The Headliners delivers a thrilling co-op horror adventure with its dark humor and team-based action, celebrating a unique journalistic twist.'),
((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'Splitgate 2', 'https://arbcdn.techfast.vn/5f7165b57dd9468fb9d25644c805cb1f.webp', 'Splitgate 2 delivers a thrilling arena shooter experience with its innovative portals and fluid movement, celebrating a unique combat twist.'),
((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'Sonic Rumble', 'https://arbcdn.techfast.vn/4ef2c94c413e4f548f778921f92016a5.webp', 'Sonic Rumble delivers a thrilling multiplayer party experience with its innovative portals and Sonic charm, celebrating a unique battle royale twist.'),
((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'Subway Surfers', 'https://arbcdn.techfast.vn/85644e90524741d984ba006bb3f98454.webp', 'Subway Surfers remains a mobile gaming icon, delivering addictive runs with vibrant visuals and power-ups, perfect for casual play.'),
((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'R.E.P.O.', 'https://arbcdn.techfast.vn/e78ce9d224384adc8a0ec70266329423.webp', 'R.E.P.O. offers a gripping cooperative horror experience with its physics-based gameplay and monster diversity, celebrating the thrill of teamwork under pressure.'),
((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'Five Nights at Freddy''s', 'https://arbcdn.techfast.vn/907e52c686454e008567fd5fdbcc9c73.webp', 'Five Nights at Freddy''s remains a groundbreaking indie horror classic, delivering tense survival and atmospheric dread through its innovative power-management mechanic and jump scares.'),
((SELECT id FROM games_page WHERE page_index=6 LIMIT 1), 'Into the Dead: Our Darkest Days', 'https://arbcdn.techfast.vn/add4c203397f455ca079d844147291f0.webp', 'Into the Dead: Our Darkest Days delivers a thrilling survival horror experience with its tense atmosphere and strategic gameplay, celebrating a unique 1980s zombie twist.');
INSERT INTO game_thumbnail (page_id, title, img, excerpt) VALUES
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'PARANOIA PLACE', 'https://arbcdn.techfast.vn/61bc5328d6e6445588850b661d6c837a.webp', 'PARANOIA PLACE stands as a compelling indie survival horror gem, particularly for co-op fans craving tense, voice-chat-driven escapes from a demonic foe.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'House of Lost Souls', 'https://arbcdn.techfast.vn/eb43b38c44e048da8a2970648e913a05.webp', 'House of Lost Souls is a haunting, if flawed, psychological horror experience that excels in atmosphere and emotional storytelling but stumbles with technical issues and limited gameplay depth.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'Blue Prince', 'https://arbcdn.techfast.vn/d043c23b20404e27849e2eea4fc6ba5b.webp', 'Blue Prince is a genre-defying masterpiece that blends roguelike strategy, puzzle-solving, and narrative mystery into an unforgettable experience.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'The Sims™ 4', 'https://arbcdn.techfast.vn/e7444d3686444523a2d9c8af84531efe.webp', 'The Sims™ 4 is a captivating life simulator that excels in creativity, customization, and emotional storytelling, offering endless possibilities for builders and storytellers.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'Look Outside', 'https://arbcdn.techfast.vn/ea980a1d126c4251bd69440b2bb34bdf.webp', 'Look Outside is a hauntingly brilliant indie horror RPG that blends retro aesthetics with modern psychological dread.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'Zoonomaly', 'https://arbcdn.techfast.vn/0b88fb2050c0445ea6f0e46d8a4f5420.webp', 'Zoonomaly is a compact, puzzle-driven indie horror game that delivers a strong sense of atmosphere and tension.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'Guilty as Sock!', 'https://arbcdn.techfast.vn/cd882199364d4358986c1f66ea3e666c.webp', 'Guilty as Sock! delivers a hilarious party game experience with its absurd trials and cooperative chaos, celebrating a unique sock puppet twist.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'Sonic Racing: CrossWorlds', 'https://arbcdn.techfast.vn/f87d6fd357764d05b293dfc6e392c6bd.webp', 'Sonic Racing: CrossWorlds is Sega’s most ambitious racing project yet, blending Sonic’s speed with creative mechanics and a surprisingly narrative-driven campaign.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'Marvel''s Spider-Man 2', 'https://arbcdn.techfast.vn/2d5a97a4ee094dd282e45329a92b8f67.webp', 'Marvel’s Spider-Man 2 is a triumphant step forward for Insomniac and superhero gaming.'),
((SELECT id FROM games_page WHERE page_index=7 LIMIT 1), 'Arma Reforger', 'https://arbcdn.techfast.vn/9e01446d086f4a03b5bd88b38b6689f6.webp', 'Arma Reforger is both an homage to the series’ origins and a forward-looking experiment in military simulation.');
INSERT INTO game_thumbnail (page_id, title, img, excerpt) VALUES
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'Dead Island 2', 'https://arbcdn.techfast.vn/32cf9147574347369e800157da465b3c.webp', 'Dead Island 2 doesn’t reinvent the zombie genre, but it perfects the art of zombie smashing. Its focus on melee combat, grotesque gore, and humorous setting make it a fun and chaotic experience, especially with friends.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'CookieRun: Kingdom', 'https://arbcdn.techfast.vn/3f3e26be23d340248735273dd1ac4b24.webp', 'Cookie Run: Kingdom is more than just a cute mobile game—it’s a cleverly designed blend of RPG depth, city-building strategy, and charming storytelling.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'Forza Motorsport', 'https://arbcdn.techfast.vn/ff66a962184940c785f048eca5fa5a0c.webp', 'Forza Motorsport is a love letter to motorsport simulation, stripping away the casual festival atmosphere of Horizon in favor of pure, competitive track racing.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'Call of Duty®: Black Ops 6', 'https://arbcdn.techfast.vn/04492dc178144cb0b877ace3adaa72c0.webp', 'Call of Duty: Black Ops 6 successfully cements itself as a worthy continuation of the Black Ops saga.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'Forza Horizon 5', 'https://arbcdn.techfast.vn/f36417175fc94d639824147edfc4dab0.webp', 'Forza Horizon 5 is more than just a racing game - it is a celebration of cars, culture, and community. Its vibrant recreation of Mexico, diverse gameplay systems, and technical brilliance elevate it to new heights within the genre.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'Grand Theft Auto: Vice City', 'https://arbcdn.techfast.vn/e0531dcb6b084d04b0dcba435305dc56.webp', 'Grand Theft Auto: Vice City is not just a game—it’s a time capsule. By blending stylish visuals, an unforgettable soundtrack, and groundbreaking freedom, Rockstar delivered an experience that shaped modern gaming.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'NBA 2K26', 'https://arbcdn.techfast.vn/478c423357f845f6a3259f371fd90e09.webp', 'NBA 2K26 is a new basketball game that delivers another powerful entry in the series, refining core mechanics while expanding story depth and social interactivity.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'FATAL FURY: City of the Wolves', 'https://arbcdn.techfast.vn/d1688e7ed20242c49ac5978ec657435c.webp', 'Fatal Fury: City of the Wolves delivers stunning cel-shaded visuals, strategic REV combat, and RPG-style solo modes—but fights guest cameo controversies and sparse content.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'Red Dead Redemption 2', 'https://arbcdn.techfast.vn/dd2a7d21ec364de0b8bee2b987eed477.webp', 'Red Dead Redemption 2 delivers an immersive open-world Western adventure with stunning visuals, rich storytelling, and deep gameplay mechanics.'),
((SELECT id FROM games_page WHERE page_index=8 LIMIT 1), 'VRChat', 'https://arbcdn.techfast.vn/40de9928ba064232854736975eee77dd.webp', 'VRChat is not a traditional video game. It is a social platform, creative canvas, and cultural phenomenon wrapped into one.');
INSERT INTO game_thumbnail (page_id, title, img, excerpt) VALUES
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'RuneScape: Dragonwilds', 'https://arbcdn.techfast.vn/af5e9c61f3e94d13b3ce689093ae160a.webp', 'RuneScape: Dragonwilds is an ambitious leap forward. While not without flaws, it successfully blends classic RuneScape identity with modern MMO expectations.'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'The Elder Scrolls IV: Oblivion Remastered', 'https://arbcdn.techfast.vn/e2f833a1270c43e1b5278b640846e55a.webp', 'The Elder Scrolls IV: Oblivion Remastered strikes a careful balance between nostalgia and modernization.'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'Wuthering Waves', 'https://arbcdn.techfast.vn/9ca38620ce8d433c9d0e84fd7b42fec3.webp', 'Wuthering Waves is a bold, stylish RPG that pushes the boundaries of what a gacha title can offer'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'Yu-Gi-Oh! Master Duel', 'https://arbcdn.techfast.vn/60ddb9acf22b4efb89ae3368f43c77c0.webp', 'Yu-Gi-Oh! Master Duel succeeds in bringing the beloved trading card game into the modern era with polish, depth, and accessibility'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'Dude Theft Wars FPS Open world', 'https://arbcdn.techfast.vn/37b0263f34c84fc6af573225c7a9f077.webp', 'Dude Theft Wars excels on the strength of sheer absurdity. It distills sandbox fun into physics-driven chaos that feels refreshing and spontaneous'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'Roblox: Dead Rails', 'https://arbcdn.techfast.vn/48fa16192cbe43f5abca104efa632cfb.webp', 'Roblox: Dead Rails transforms a familiar survival formula into a uniquely explosive experience: riding a train into the zombie apocalypse, scavenging fuel, fighting threats, and facing a final boss in a procedural Wild West nightmare'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'EA SPORTS FC™ 26', 'https://arbcdn.techfast.vn/0a2685c996b04bc7a665dcea70a7c9f1.webp', 'EA SPORTS FC™ 26 isn’t about grand spectacle—it’s about fine-tuning. Through deliberate enhancements—from tighter dribbling to dual-mode presets, and from narrative-rich Career features to inclusive competitive formats—it achieves a balanced evolution in football gaming'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'RimWorld: Odyssey', 'https://arbcdn.techfast.vn/8a388da701884d0fa9b83b7161a7348a.webp', 'A masterclass in big expansion strategy—expansive, imaginative, and technically impressive. Odyssey ensures RimWorld remains not just relevant, but essential.'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'Grand Theft Auto VI', 'https://arbcdn.techfast.vn/b9d3cd49454e4246b66b6ba3aed36ed4.webp', 'Grand Theft Auto VI represents not just a new chapter for Rockstar but a turning point for the open-world genre'),
((SELECT id FROM games_page WHERE page_index=9 LIMIT 1), 'Schedule I', 'https://arbcdn.techfast.vn/4e4e0cb83f62429097bb283472623784.webp', 'Schedule I reinvents tycoon simulation with boldness, humor, and slick gameplay loops. Its success shows the power of indie creativity on a global stage');
